import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import "./App.css";

function ChatApp() {
  const [message, setMessage] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const chatContainerRef = useRef(null);
  const [response, setResponse] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [file, setFile] = useState(null);
  const [fileBase64, setFileBase64] = useState(null);  // ✅ Add this
  const [fileName, setFileName] = useState("");        
  useEffect(() => {
    chatContainerRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [chatHistory]);

  const handleInputChange = (event) => {
    setMessage(event.target.value);
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
        setFileName(file.name); // Store file name

        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = () => {
            const base64String = reader.result.split(",")[1]; // Remove data prefix
            setFileBase64(base64String);

            console.log("Encoded Base64:", base64String.substring(0, 100) + "..."); // Debugging
        };
        reader.onerror = (error) => {
            console.error("Error reading file:", error);
        };
    }
};

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);
    setError("")

    if (!message.trim()) return;

    setLoading(true);
    setError("");  // Reset any previous errors
    setResponse(""); // Reset previous response

    try {
      if (!message || !fileBase64) {
          setError("Please enter a message and upload a file.");
          setLoading(false);
          return;
      }

      const requestData = {
          message: message,
          file_name: fileName,
          file_base64: fileBase64 ,
      };

      console.log("Sending Data to Flask:", requestData); // Debugging

      const res = await axios.post("http://127.0.0.1:5000/chat", requestData, {
          headers: { "Content-Type": "application/json" },
      });

      console.log("AI Response:", res.data.response);
      setResponse(res.data.response);
  } catch (err) {
      setError("Something went wrong. Please try again.");
      console.error("API Error:", err);
  } finally {
      setLoading(false);
  }
};

  return (
    <div className="chat-container">
      <h1>Resume optimized</h1>
      <div className="chat-box">
        {loading && <div className="chat-message ai">Typing...</div>}
        <div ref={chatContainerRef}>{response && (
        <div className="response">
          <p>{response}</p>
        </div>
      )}</div>
      </div>
      <form onSubmit={handleSubmit} className="chat-form">
        <input
          type="text"
          value={message}
          onChange={handleInputChange}
          placeholder="Ask me anything..."
          className="chat-input"
        />
        <input type="file" onChange={handleFileChange} accept=".txt,.pdf,.docx,.csv" />
        <button type="submit" className="chat-button" disabled={loading}>
          {loading ? "Thinking..." : "Send"}
        </button>
        {error && <p className="error">{error}</p>}
      </form>
    </div>
  );
}

export default ChatApp;
