import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import "./App.css";

function ChatApp() {
  const [message, setMessage] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
 // const chatContainerRef = useRef(null);
  const [response, setResponse] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [fileBase64, setFileBase64] = useState(null);  // ✅ Add this
  const [fileName, setFileName] = useState("");        
  const [jobTitle, setJobTitle] = useState("");
  const [jobDescription, setJobDescription] = useState("");

  // useEffect(() => {
  //   chatContainerRef.current?.scrollIntoView({ behavior: "smooth" });
  // }, [chatHistory]);



  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
        setFileName(file.name); // Store file name

        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = () => {
            const base64String = reader.result.split(",")[1]; // Remove data prefix
            setFileBase64(base64String);

            console.log("Encoded Base64:", base64String.substring(0, 100) + "..."); // Debugging
        };
        reader.onerror = (error) => {
            console.error("Error reading file:", error);
        };
    }
};

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);
    setError("")


    setLoading(true);
    setError("");  // Reset any previous errors
    setResponse(""); // Reset previous response

    try {
      if (!jobTitle || !jobDescription || !fileBase64) {
          setError("Please enter a Job Tile, Job Description and upload a file.");
          setLoading(false);
          return;
      }

      const requestData = {
          message: jobTitle + ", " + jobDescription + "\n" + " Please analyze this resume and provide detailed feedback on what should be improved to pass ATS screening and appeal to recruiters." ,
          file_name: fileName,
          file_base64: fileBase64 ,
      };

      console.log("Sending Data to Flask:", requestData); // Debugging

      const res = await axios.post("http://127.0.0.1:5000/chat", requestData, {
          headers: { "Content-Type": "application/json" },
      });

      console.log("AI Response:", res.data.response);
      setResponse(res.data.response);
  } catch (err) {
      setError("Something went wrong. Please try again.");
      console.error("API Error:", err);
  } finally {
      setLoading(false);
  }
};

  return (
    <div className="container">
      <h1>AI Resume Analyzer</h1>
      <form onSubmit={handleSubmit}>
        <label>Job Title:</label>
        <input
          type="text"
          value={jobTitle}
          onChange={(e) => setJobTitle(e.target.value)}
          required
        />

        <label>Job Description:</label>
        <textarea
          value={jobDescription}
          onChange={(e) => setJobDescription(e.target.value)}
          required
          rows="4"
        />

        <label>Upload Resume (PDF/DOCX):</label>
        <input type="file" onChange={handleFileChange} accept=".pdf,.docx" required />

        <button type="submit" disabled={loading}>
          {loading ? "Processing..." : "Analyze Resume"}
        </button>

        {error && <p className="error">{error}</p>}
      </form>
    </div>
  );
}

export default ChatApp;
