import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import "./App.css";

function ChatApp() {
  const [message, setMessage] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const chatContainerRef = useRef(null);
  const [response, setResponse] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");


  useEffect(() => {
    chatContainerRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [chatHistory]);

  const handleInputChange = (event) => {
    setMessage(event.target.value);
  };
  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!message.trim()) return;

    setLoading(true);
    setError("");  // Reset any previous errors
    setResponse(""); // Reset previous response

    try {
      const res = await axios.post("http://127.0.0.1:5000/chat", {
        message: message,
    }, {
        headers: { "Content-Type": "application/json" }
    });

      // Ensure response is properly formatted
      setChatHistory((prevChat) => [...prevChat, res.data.response]);
      if (res.data && res.data.response) {
        setResponse(res.data.response);
      } else {
        setError("Unexpected response format from server.");
      }

    } catch (err) {
      console.error("API Error:", err);

      // Handle different error cases
      if (err.response) {
        // Server responded with a status code outside 2xx range
        setError(`Server Error: ${err.response.data.error || err.response.status}`);
      } else if (err.request) {
        // Request was made but no response received
        setError("No response from the server. Check if Flask is running.");
      } else {
        // Something happened while setting up the request
        setError(`Error: ${err.message}`);
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="chat-container">
      <h1>Resume optimized</h1>
      <div className="chat-box">
        {loading && <div className="chat-message ai">Typing...</div>}
        <div ref={chatContainerRef}>{response && (
        <div className="response">
          <p>{response}</p>
        </div>
      )}</div>
      </div>
      <form onSubmit={handleSubmit} className="chat-form">
        <input
          type="text"
          value={message}
          onChange={handleInputChange}
          placeholder="Ask me anything..."
          className="chat-input"
        />
        <button type="submit" className="chat-button" disabled={loading}>
          {loading ? "Thinking..." : "Send"}
        </button>

      </form>
    </div>
  );
}

export default ChatApp;
